//Validation
//
def getTestStep = testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["GetEmployee"];

def getEmployeeContext = new WsdlTestRunContext(getTestStep);
getTestStep.run(testRunner,getEmployeeContext)

def getRes = testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["GetEmployee"].getPropertyValue("Response")
def xmlGetres = new XmlHolder(getRes)
def Getresponse = xmlGetres.getNodeValue("//ns:name")
log.info Getresponse
//log.info name
def name = testRunner.testCase.testSuite.getPropertyValue("name")
assert Getresponse == name

